import React, { useState, useEffect } from "react";
import InputTextField from "../Components/TextField/TextField";
import Buttons from "../Components/Button/Button";
import Card from "@material-ui/core/Card";
import axios from "axios";
import { makeStyles } from "@material-ui/core";

const useStyles = makeStyles(() => ({
  card: {
    displat: "flex",
    justifyContent: "center",
    border: "2px solid grey",
  },
}));

const ViewBooking = () => {
  const [bookingDetails, setBookingDetails] = useState("");
  const classes = useStyles();

  useEffect(() => {
    axios({
      method: "GET",
      url: "http://localhost:8080/displaybookings",
    }).then(() => {
      setBookingDetails(bookingDetails);
    });
  }, []);

  return (
    <div className={classes.card}>
      {bookingDetails &&
        bookingDetails.map((details) => (
          <Cards>
            <p> {details.firstName}</p>
            <p> {details.lastName}</p>
            <p> {details.bookingDate}</p>
            <p> {details.roomNumber}</p>
            <p> {details.bookingTime}</p>
          </Cards>
        ))}
    </div>
  );
};

export default ViewBooking;
